var ComicsObj={init:function(){document.observe("keydown",function(c){var a=c||window.event;var b=a.keyCode||a.which;if(a.ctrlKey){if("function"==typeof a.preventDefault){a.preventDefault()}else{a.returnValue=false}if(b==Event.KEY_LEFT){if($("previous_comics")){location.href=$("previous_comics").href}}else{if(b==Event.KEY_RIGHT){if($("next_comics")){location.href=$("next_comics").href}}}window.focus();return false}})}};Event.observe(window,"load",function(){ComicsObj.init()});