var ComicsObj={init:function(){document.observe("keydown",function(d){var a=d||window.event;var c=a.keyCode||a.which;if(a.ctrlKey){var b=false;if(c==Event.KEY_LEFT){if($("previous_comics")){location.href=$("previous_comics").href;b=true}}else{if(c==Event.KEY_RIGHT){if($("next_comics")){location.href=$("next_comics").href;b=true}}}if(b){if("function"==typeof a.preventDefault){a.preventDefault()}else{a.returnValue=false}}window.focus();return false}})}};Event.observe(window,"load",function(){ComicsObj.init()});